mapper.update:
UPDATE BH_MEMBER SET password = #{password}, name = #{name}, phone = #{phone},
email = #{email}, point = #{point}, zip = #{zip}, addr1 = #{addr1}, addr2 = #{addr2}, role = #{role},
wDate = #{wDate}, rDate = now() WHERE id = #{id}

mapper.select:
SELECT COUNT(id)
FROM BH_MEMBER
WHERE id = #{id}
